package org.busywait.d1

import utest._

object CaptchaTests extends TestSuite {
  override def tests: Tests = Tests {
    val input = ""

    'NextDigit - {
      "Test 1122 correctly counts to three" - {
        val captcha = new Captcha("1122")

        val value = captcha.captchaFP()
        assert(value == 3)
      }

      //    "Test 1111 correctly counts to four" - {
      'test1111 - {
        val captcha = new Captcha("1111")

        val value = captcha.captchaFP()
        assert(value == 4)
      }

      "Test 1234 correctly count to zero" - {
        val captcha = new Captcha("1234")

        val value = captcha.captchaFP()
        assert(value == 0)
      }

      "Test 91212129 correctly count to nine" - {
        val captcha = new Captcha("91212129")

        val value = captcha.captchaFP()
        assert(value == 9)
      }

      "Test 991212129 correctly count to eight-ten" - {
        val captcha = new Captcha("991212129")

        val value = captcha.captchaFP()
        assert(value == 18)
      }

      "Test 9912112129 correctly count to nine-teen" - {
        val captcha = new Captcha("9912112129")

        val value = captcha.captchaFP()
        assert(value == 19)
      }

      "Test 99121121288 correctly count to eight-ten" - {
        val captcha = new Captcha("99121121288")

        val value = captcha.captchaFP()
        assert(18 == 18)
      }

      "Test that it correctly solves puzzle" - {
        val captcha = new Captcha(input)

        val value = captcha.captchaFP()
        println(value)

        assert(value == 1069)
      }
    }

    'HalfwayAround - {
      "Test 1212 correctly counts to six" - {
        val captcha = new Captcha("1212")

        val value = captcha.captchaHalfway()
        assert(value == 6)
      }

      "Test 1221 correctly counts to zero" - {
        val captcha = new Captcha("1221")

        val value = captcha.captchaHalfway()
        assert(value == 0)
      }

      "Test 123425 correctly counts to four" - {
        val captcha = new Captcha("123425")

        val value = captcha.captchaHalfway()
        assert(value == 4)
      }

      "Test 123123 correctly counts to twelwe" - {
        val captcha = new Captcha("123123")

        val value = captcha.captchaHalfway()
        assert(value == 12)
      }

      "Test 12131415 correctly counts to four" - {
        val captcha = new Captcha("12131415")

        val value = captcha.captchaHalfway()
        assert(value == 4)
      }

      "Test that it correctly solves puzzle" - {
        val captcha = new Captcha(input)

        val value = captcha.captchaHalfway()
        println(value)

        assert(value == 1268)
      }
    }
  }
}
